mapper.update:
UPDATE mmall_cart
set quantity=#{count},update_time=now()
where user_id = #{userId} and product_id=#{productId}

UPDATE mmall_cart
SET  checked = 1
WHERE  product_id=#{productId} and user_id=#{userId}

UPDATE mmall_cart
SET  checked = 0
WHERE  product_id=#{productId} and user_id=#{userId}

mapper.select:
SELECT count(*)
from mmall_cart
where user_id=#{userId}

mapper.update:
UPDATE mmall_cart
set checked=1
where user_id=#{userId}

UPDATE mmall_cart
set checked=0
where user_id=#{userId}
